mapper.select:
select * from job

    
        city = #{city}
    
    
        and experience = #{experience}
    
    
        and educationLevel = #{educationlevel}
    

    limit #{pageNum}, #{pageSize}

select count(*) from job

    
        city = #{city}
    
    
        and experience = #{experience}
    
    
        and educationLevel = #{educationlevel}
